mapper.select:
SELECT * FROM (SELECT PG.*, CEIL(ROWNUM/12) AS PAGENO FROM (SELECT PB.* FROM (SELECT PB.PB_NO ,CREATOR_ID ,PB_TITLE ,PB_CREATE_DATE ,PB_UPDATE_DATE ,(SELECT COUNT(*) FROM PB_RECOMMEND PBR WHERE PB.PB_NO = PBR.PB_NO) AS PB_RECOMMAND_COUNT ,PB_VISIT_COUNT ,PFL.PB_FNO AS pb_thumbnail_fno FROM PHOTO_BOARD PB, (SELECT PB_NO, MIN(PB_FNO) AS PB_FNO FROM PB_FILE_LIST GROUP BY PB_NO) PFL WHERE PB.PB_NO = PFL.PB_NO(+) ORDER BY PB.PB_NO DESC) PB) PG WHERE
PB_TITLE LIKE '%' || #{search} || '%'
PB_TITLE LIKE '%' || #{search} || '%' AND PB_CONTENT LIKE '%' || #{search} || '%'
CREATOR_ID LIKE '%' || #{search} || '%'
) WHERE PAGENO = #{pageNo}
